 8	
 9	@Slf4j
 10	@RequestMapping("/emps")
 11	@RestController
 12	public class EmpController {
 13	 @Autowired
 14	 EmpService empService;
 15	
 16	 /**
 17	 * 分页查询员工信息
 18	 * @param param 查询参数对象
 19	 * @return 分页结果
 20	 */
