 22	 public Result page(EmpQueryParam param) {
 23	 log.info("分页查询员工,参数:{}", param);
 24	 PageResult<Emp> pageResult = empService.page(param);
 25	 return Result.success(pageResult);
 26	 }
 27	
 28	 @PostMapping
 29	 public Result addEmp(@RequestBody Emp emp){
 30	 log.info("添加员工,参数:{}", emp);
 31	 empService.addEmp(emp);
 32	 return Result.success();
 33	 }
 34	}
