 6	 <select id="list" resultType="com.yzy.pojo.Emp">
 7	 SELECT emp.*, dept.name deptName
 8	 FROM emp
 9	 LEFT JOIN dept ON emp.dept_id = dept.id
 10	 <where>
 11	 <if test="name != null and name != ''">
 12	 emp.name LIKE CONCAT('%', #{name}, '%')
 13	 </if>
 14	 <if test="gender != null">
 15	 AND emp.gender = #{gender}
 16	 </if>
 17	 <if test="begin != null">
 18	 AND emp.entry_date &gt;= #{begin}
 19	 </if>
 20	 <if test="end != null">
 21	 AND emp.entry_date &lt;= #{end}
 22	 </if>
 23	 </where>
 24	 ORDER BY emp.update_time DESC
 25	 </select>
