 14	import java.time.LocalDate;
 15	import java.time.LocalDateTime;
 16	import java.util.List;
 17	
 18	@Service
 19	public class EmpServiceImpl implements EmpService {
 20	 @Autowired
 21	 private EmpMapper empMapper;
 22	 private EmpExpr empExprMapper;
 23	
 24	 @Override
 25	 @Deprecated
 26	 public PageResult<Emp> page(Integer page, Integer pageSize, String name, Integer gender, LocalDate begin, LocalDate end) {
 27	 PageHelper.startPage(page,pageSize);
 28	 List<Emp> list = empMapper.list(name,gender,begin,end);
 29	 Page<Emp> p = (Page<Emp>) list;
 30	 return new PageResult<>(p.getTotal(),p.getResult());
 31	 }
 32	 
 33	 @Override
 34	 public PageResult<Emp> page(EmpQueryParam param) {
 35	// // 设置默认值
 36	// if (param.getPage() == null || param.getPage() < 1) {
 37	// param.setPage(1);
 38	// }
 39	// if (param.getPageSize() == null || param.getPageSize() < 1) {
 40	// param.setPageSize(10);
 41	// }
 42	 
 43	 PageHelper.startPage(param.getPage(), param.getPageSize());
 44	 List<Emp> list = empMapper.list(param.getName(), param.getGender(), param.getBegin(), param.getEnd());
 45	 Page<Emp> p = (Page<Emp>) list;
 46	 return new PageResult<>(p.getTotal(), p.getResult());
 47	 }
 48	
 49	 @Override
 50	 public void addEmp(Emp emp) {
 51	 emp.setCreateTime(LocalDateTime.now());
 52	 emp.setUpdateTime(LocalDateTime.now());
 53	 empMapper.addEmp(emp);
 54	
 55	 List<EmpExpr> empExpr = emp.getEmpExpr();
 56	 if(!empExpr.isEmpty()){
 57	 empExprMapper.addBatch(empExpr);
 58	 }
 59	 }
 60	}
