 33	 SELECT emp.*, dept.name deptName
 34	 FROM emp
 35	 LEFT JOIN dept ON emp.dept_id = dept.id
 36	 <where>
 37	 <if test="name != null and name != ''">
 38	 emp.name LIKE CONCAT('%', #{name}, '%')
 39	 </if>
 40	 <if test="gender != null">
 41	 AND emp.gender = #{gender}
 42	 </if>
 43	 <if test="begin != null">
 44	 AND emp.entry_date &gt;= #{begin}
 45	 </if>
 46	 <if test="end != null">
 47	 AND emp.entry_date &lt;= #{end}
 48	 </if>
 49	 </where>
 50	 ORDER BY emp.update_time DESC
 51	 </select>
