 48	// param.setPageSize(10);
 49	// }
 50	 
 51	 PageHelper.startPage(param.getPage(), param.getPageSize());
 52	 List<Emp> list = empMapper.list(param.getName(), param.getGender(), param.getBegin(), param.getEnd());
 53	 Page<Emp> p = (Page<Emp>) list;
 54	 return new PageResult<>(p.getTotal(), p.getResult());
 55	 }
 56	
 57	 @Transactional //事务管理注解，如果这个方法运行成功则提交，如果有错误，则回滚
 58	 @Override
 59	 public void addEmp(Emp emp) {
 60	 emp.setCreateTime(LocalDateTime.now());
 61	 emp.setUpdateTime(LocalDateTime.now());
 62	 empMapper.addEmp(emp);
 63	
 64	 log.info("员工插入后的ID: {}", emp.getId());
 65	
 66	 List<EmpExpr> empExpr = emp.getExprList();
 67	 log.info("工作经历列表: {}", empExpr);
 68	
 69	 if(empExpr != null && !empExpr.isEmpty()){
 70	 log.info("开始处理工作经历，数量: {}", empExpr.size());
 71	 // 为每个工作经历设置员工ID
 72	 for(EmpExpr expr : empExpr) {
 73	 expr.setEmpId(emp.getId());
 74	 log.info("设置工作经历的员工ID: {}", expr);
 75	 }
 76	 empExpeMapper.addBatch(empExpr);
 77	 log.info("工作经历批量插入完成");
 78	 } else {
 79	 log.info("工作经历列表为空，跳过插入");
 80	 }
 81	 }
 82	
 83	 @Transactional
 84	 @Override
 85	 public void deleteEmp(List<Long> ids) {
 86	 empMapper.deleteByIds(ids);
 87	 empExpeMapper.deleteEmpExprByEmpIds(ids);
 88	 }
 89	
 90	 @Override
 91	 public Emp getInfo(Integer id) {
 92	 return empMapper.getInfo(id);
 93	
 94	 }
 95	
 96	 @Override
 97	 public void update(Emp emp) {
 98	 emp.setUpdateTime(LocalDateTime.now());
 99	 empMapper.update(emp);
 100	
 101	 //将id转化为List<Long>
 102	 List<Long> Array = new ArrayList<>();
 103	 Array.add(Long.valueOf(emp.getId()));
 104	 empExpeMapper.deleteEmpExprByEmpIds(Array);
 105	
 106	 List<EmpExpr> empExpr = emp.getExprList();
 107	 if (empExpr != null && !empExpr.isEmpty()){
 108	 empExpr.forEach(expr -> expr.setEmpId(emp.getId()));
 109	 empExpeMapper.addBatch(empExpr);
 110	 }
 111	
 112	 }
 113	}
