 9	 <if test="username != null and username != ''">username = #{username},</if>
 10	 <if test="name != null and name != ''">name = #{name},</if>
 11	 <if test="gender != null">gender = #{gender},</if>
 12	 <if test="phone != null and phone != ''">phone = #{phone},</if>
 13	 <if test="job != null and job != ''">job = #{job},</if>
 14	 <if test="salary != null">salary = #{salary},</if>
 15	 <if test="entryDate != null">entry_date = #{entryDate},</if>
 16	 <if test="image != null and image != ''">image = #{image},</if>
 17	 <if test="deptId != null">dept_id = #{deptId},</if>
 18	 update_time = #{updateTime}
 19	 </set>
 20	 WHERE id = #{id}
 21	
 22	 </update>
 23	
 24	 <delete id="deleteByIds">
 25	 DELETE FROM emp
 26	 WHERE id IN
 27	 <foreach collection="ids" item="id" open="(" separator="," close=")">
 28	 #{id}
 29	 </foreach>
 30	 </delete>
 31	
 32	 <select id="list" resultType="com.yzy.pojo.Emp">
 33	 SELECT emp.*, dept.name deptName
 34	 FROM emp
 35	 LEFT JOIN dept ON emp.dept_id = dept.id
 36	 <where>
 37	 <if test="name != null and name != ''">
 38	 emp.name LIKE CONCAT('%', #{name}, '%')
 39	 </if>
 40	 <if test="gender != null">
 41	 AND emp.gender = #{gender}
 42	 </if>
 43	 <if test="begin != null">
 44	 AND emp.entry_date &gt;= #{begin}
 45	 </if>
 46	 <if test="end != null">
 47	 AND emp.entry_date &lt;= #{end}
 48	 </if>
 49	 </where>
 50	 ORDER BY emp.update_time DESC
 51	 </select>
