 88	 ee.company AS ee_company,
 89	 ee.job AS ee_job
 90	 FROM emp e
 91	 LEFT JOIN dept d ON e.dept_id = d.id
 92	 LEFT JOIN emp_expr ee ON e.id = ee.emp_id
 93	 WHERE e.id = #{id}
 94	 </select>
 95	 <!-- 统计员工职位人数 -->
 96	 <select id="getEmpJobData" resultType="java.util.Map">
 97	 SELECT
 98	 (CASE WHEN job=1 THEN '班主任'
 99	 WHEN job=2 THEN '讲师'
 100	 WHEN job=3 THEN '学工主管'
 101	 WHEN job=4 THEN '教研主管'
 102	 WHEN job=5 THEN '咨询师'
 103	 ELSE '其他' END) pos,
 104	 COUNT(*) num
 105	 FROM emp
 106	 GROUP BY job
 107	 ORDER BY num
 108	 </select>
