 8	 INSERT INTO emp_expr (emp_id, begin, end, company, job)
 9	 VALUES
 10	 <foreach collection="list" item="empExpr" separator=",">
 11	 (#{empExpr.empId}, #{empExpr.begin}, #{empExpr.end}, #{empExpr.company}, #{empExpr.job})
 12	 </foreach>
 13	 </insert>
