 SELECT
 (CASE WHEN gender=1 THEN '男性员工'
 WHEN gender=2 THEN '女性员工'
 ELSE '其他' END) name,
 COUNT(*) value
 FROM emp
 GROUP BY gender
 ORDER BY value
</select>
