 8	 INSERT INTO emp_expr (emp_id, begin, end, company, job)
 9	 VALUES
 10	 <foreach collection="list" item="empExpr" separator=",">
 11	 (#{empExpr.empId}, #{empExpr.begin}, #{empExpr.end}, #{empExpr.company}, #{empExpr.job})
 12	 </foreach>
 13	 </insert>
 14	
 15	 <delete id="deleteEmpExprByEmpIds">
 16	 DELETE FROM emp_expr
 17	 WHERE emp_id IN
 18	 <foreach collection="ids" item="id" open="(" separator="," close=")">
 19	 #{id}
 20	 </foreach>
 21	 </delete>
 22	
 23	 <update id="updateEmpExpr">
 24	 <foreach collection="exprList" item="expr" separator=";">
 25	 UPDATE emp_expr
 26	 SET begin = #{expr.begin},
 27	 end = #{expr.end},
 28	 company = #{expr.company},
 29	 job = #{expr.job}
 30	 WHERE id = #{expr.id}
